import React, { useEffect, useState } from 'react';
import { StreamChat } from 'stream-chat';
import {Chat,Channel,ChannelHeader,ChannelList,LoadingIndicator,MessageInput,MessageList,Thread,Window,} from 'stream-chat-react';

// import 'stream-chat-react/dist/css/index.css';

const userToken =
  'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoidGhyb2JiaW5nLWNyZWRpdC01In0.aoDwXw4pzJfNUdTjgO5BKNrr23hPEWdDJRn-x8ccMd0';

const filters = {
  type: 'messaging',
  members: { $in: ['throbbing-credit-5'] },
};
const sort = { last_message_at: -1 };

const ChatApp = (props) => {
  const [chatClient, setChatClient] = useState(null);

  useEffect(() => {
    const initChat = async () => {
      const client = StreamChat.getInstance('chbyjnv2st7v');

      await client.connectUser(
        {
          id: props.username,
          name: props.username,
          image:
            'https://www.google.com/search?q=user+photo+icon&rlz=1C1JJTC_enIN959IN959&sxsrf=APq-WBup4X8kIhuOqjP74aQcgc0az4SmvQ:1644859842892&tbm=isch&source=iu&ictx=1&vet=1&fir=5EMYCfbP3D3XuM%252Cwm3m253FOCCAUM%252C_%253BKnR9uI1e8d4qTM%252CIuY_NaZ2k4MpJM%252C_%253BP6AlQsrs5ks3RM%252CnKbZ7-T8tpkWLM%252C_%253BAK6Do3uj3k0n4M%252C3rfKY1BtP7l6AM%252C_%253BpOq9fvJabj6E8M%252C3rfKY1BtP7l6AM%252C_%253BJZdAUIqMJv3ItM%252CLW4IfpxfOiKCbM%252C_%253BDTjpu7UoNmhmwM%252C59BfzthfWV84HM%252C_%253BgzW4aEW--xWE9M%252CwqVxkAB5BpC03M%252C_%253BCM17TkKiXGhsSM%252CIkTvTGO-3Z9yKM%252C_%253BYIG-EMKSk-GO4M%252C3rfKY1BtP7l6AM%252C_%253Bz3fL0hiievBn9M%252CyMN2RtNt21G3uM%252C_%253BAgu8-ECdH8TgqM%252CLpuS0x_nac3LYM%252C_%253BQE3OQDJuT9zyvM%252CwU-UBXFj7PPJ2M%252C_%253B0ntpHPwBnS7bSM%252CSfH4NXm9bbWkzM%252C_%253BSBUlgl2FWFFpQM%252CvEf3EbQkmklkOM%252C_%253Bmwsp2tPDl9b3xM%252C3rfKY1BtP7l6AM%252C_&usg=AI4_-kRP0J7SFZV4R6pZa7FzpwjllZQm7g&sa=X&ved=2ahUKEwiV5I-13P_1AhX-TGwGHbKTC00Q9QF6BAgBEAE#imgrc=5EMYCfbP3D3XuM',
        },
        userToken
      );

      setChatClient(client);
    };

    initChat();
  }, []);

  if (!chatClient) {
    return <LoadingIndicator />;
  }

  return (
    <Chat client={chatClient} theme="messaging light">
      <ChannelList filters={filters} sort={sort} />
      <Channel>
        <Window>
          <ChannelHeader />
          <MessageList />
          <MessageInput />
        </Window>
        <Thread />
      </Channel>
    </Chat>
  );
};

export default ChatApp ;
